package com.yskj.api;import com.yskj.controller.base.BaseController;import com.yskj.models.PageParam;import com.yskj.models.QueryParam;import com.yskj.models.Metro;import com.yskj.service.MetroService;import com.yskj.service.StationService;import com.yskj.utils.Result;import org.apache.shiro.authz.annotation.RequiresPermissions;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.*;import org.slf4j.Logger;import org.slf4j.LoggerFactory;@Controller@RequestMapping(value = "/api/MetroController")public class ApiMetroController extends BaseController{	@Autowired	private MetroService metroService;	@Autowired    private StationService stationService;	private final static Logger logger = LoggerFactory.getLogger(ApiMetroController.class);	 public MetroService getService() {         return this.metroService;     }	 /**     * 页面     * @return String     */    @RequiresPermissions("Metro")    @RequestMapping(value = "",method = RequestMethod.GET, produces = {"text/html; charset=utf-8"})    public String index(Model model){        return "metro";    }     /**     *新增     * @param metro     * @return Result     */    @RequestMapping(value = "/add", method = RequestMethod.POST, produces = {"application/json; charset=utf-8"})    @ResponseBody    public Result add(Metro metro ){         return super.add(metro);    }    /**     * 删除     * @param metro     * @return Result     */    @RequestMapping(value = "/delete", method = RequestMethod.POST, produces = {"application/json; charset=utf-8"})    @ResponseBody    public Result delete(Metro metro ){        return super.delete(metro);    }     /**     * 修改     * @param metro     * @return Result     */    @RequestMapping(value = "/update", method = RequestMethod.POST, produces = {"application/json; charset=utf-8"})    @ResponseBody    public Result update(Metro metro ){        return super.update(metro);    }    /**     * 查询页面     * @param pageParam     * @return Result     */    @RequestMapping(value = "/findPage", method = RequestMethod.POST, produces = {"application/json; charset=utf-8"})    @ResponseBody    public Result findPage(@RequestBody PageParam pageParam ){       return super.findPage(pageParam);    }     /**     * 查询集合     * @param queryParam     * @return Result     */    @RequestMapping(value = "/findList", method = RequestMethod.POST, produces = {"application/json; charset=utf-8"})    @ResponseBody    public Result findList(@RequestBody QueryParam queryParam ){        return super.findList(queryParam);    }    /**    * 模糊查询页面    * @param pageParam    * @return Result    */    @RequestMapping(value = "/findLikePage", method = RequestMethod.POST, produces = {"application/json; charset=utf-8"})    @ResponseBody    public Result findLikePage(@RequestBody PageParam pageParam ){         return super.findLikePage(pageParam);    }    /**     * 查询集合，模糊查询     * @param queryParam     * @return Result     */    @RequestMapping(value = "/findLikeList", method = RequestMethod.POST, produces = {"application/json; charset=utf-8"})    @ResponseBody    public Result findLikeList(@RequestBody QueryParam queryParam ){        return super.findLikeList(queryParam);    }     /**     * 唯一查询     * @param queryParam     * @return Result     */    @RequestMapping(value = "/one", method = RequestMethod.POST, produces = {"application/json; charset=utf-8"})    @ResponseBody    public Result one(@RequestBody QueryParam queryParam ){        return super.one(queryParam);    }    /**     * 唯一查询通过ID     * @param id     * @return Result     */    @RequestMapping(value = "/get", method = RequestMethod.POST, produces = {"application/json; charset=utf-8"})    @ResponseBody    public Result get(String id ){        return super.get(id);    }    /**     * 查询集合     * @param     * @return Result     */    @RequestMapping(value = "/getMyCityMetro/{cityID}", method = RequestMethod.GET, produces = {"application/json; charset=utf-8"})    @ResponseBody    public Result getMyCityRegion( @PathVariable String cityID){        Result result = new Result();        QueryParam queryParam = new QueryParam();        queryParam.put("cityID",cityID);        try {            result.listData(metroService.getMyCityMetro(queryParam));        } catch (Exception e) {            e.printStackTrace();            logger.error(e.getMessage());            result.error("获取失败");        }        return result;    }    /**     * 查询集合     * @param     * @return Result     */    @RequestMapping(value = "/getMyCityStation/{metroID}", method = RequestMethod.GET, produces = {"application/json; charset=utf-8"})    @ResponseBody    public Result getMyCityStation( @PathVariable String metroID){        Result result = new Result();        QueryParam queryParam = new QueryParam();        queryParam.put("metroID",metroID);        queryParam.setOrderByClause(" order by s.sort ");        try {            result.listData(stationService.findList(queryParam));        } catch (Exception e) {            e.printStackTrace();            logger.error(e.getMessage());            result.error("获取失败");        }        return result;    }}