package com.yskj.models;import com.yskj.utils.MD5Tools;import lombok.Data;import java.math.BigDecimal;//表名称 account@Datapublic class Account extends BaseEntity {    public static Integer RE_QZ_BOND =1;    public static Integer QZ_SALARY =2;    public static Integer RE_ZP_BOND=3;    public static Integer ZP_SALARY =4;    public static Integer TX = 5;    public static Integer QZ_BOND_ZP = 6;    public static Integer ZP_BOND_QZ = 7;    public static Integer PARTNER_REBATE=8;    public static Integer TX_SXF = 9;    public static Integer REWARD = 10;    public static Integer REDPACKET = 11;    public static Integer REDPACKETRETURN = 12;    public static Integer TAX = 13;    public static Integer QZ_SCAN_SALARY = 14;    public static Integer YU_E_PAY = 16;    public static Integer JS_MINE_IN = 17;//入钱 结算给自己    public static Integer JS_MINE_OUT = 18;//扣钱 结算给自己    public static Integer TRANS_OUT = 19;// 转出 转账'    public static Integer RECHARGE = 29;//充值    public static Integer TRANS_IN = 30;//转入 转账	private static final long serialVersionUID = 1L;	public Account() {		super();	}	    //用户编号    private String userID;    //金额    private BigDecimal money;    //1，求职者保证金退还；2，求职者薪资收入；3，招聘者保证金退还；4，招聘者薪资发放,5，提现 ,6 求职人员违约赔付给招聘者，7招聘者赔付给求职人员,8合伙人分享获利 9提现手续费,10悬赏金,11红包收入,12红包退款 ,13税收 ,14求职者扫码结算,16用余额支付，17结算给自己（入）,18结算给自己(出)，19转出,29充值 ,30转入    private Integer type;    //true入账，false出账    private Boolean isIn;    //是不是通过的，如果是提现则开始为false 审核后true    private Boolean isPass;    //订单号，防止重复提交    private String orderNo;    private String mark;    //是不是校验过了    private Boolean isCheck;    //错误的原因是什么    private String errmsg;    //md5校验    private String md5;    private String buyname;    //提取了多少    private BigDecimal extract;    //这笔钱是哪个提现关联的    private String accID;    public void updateCheck(String msg,Boolean isOk){        this.setErrmsg(msg);        this.setIsCheck(isOk);  //钱 订单 入账 版本 uesrID type ispass        this.setMd5(MD5Tools.getObjectMd5(this.getMoney().toString(),this.getOrderNo(),this.getIsIn().toString(),this.getVersion()+1+"",this.getUserID(),this.getType().toString(),this.getIsPass().toString()));    }    public String generalMd5(){        return MD5Tools.getObjectMd5(this.getMoney().toString(),this.getOrderNo(),this.getIsIn().toString(),this.getVersion()+"",this.getUserID(),this.getType().toString(),this.getIsPass().toString());    }    public Boolean needTax(){        if(this.type==2||this.type==6||this.type==7||this.type==8||this.type==10||this.type==11||this.type==14||this.type==JS_MINE_IN){            return true;        }else{            return false;        }    }}