package com.yskj.models;import com.yskj.aop.StorageChild;import lombok.Data;import java.util.Date;import java.util.List;//表名称 appealhandle@Datapublic class Appealhandle extends BaseEntity {	private static final long serialVersionUID = 1L;	public Appealhandle() {		super();	}	    //申诉订单号  S000000001 S000000002    private String appealID;    //申诉时间    private Date appealTime;    //申诉类型(1,虚假职位信息不予结算薪酬;3,薪酬结算错误;4,其他)    private Integer appealType;    //申诉工作(关联 position表主键)    private String positionID;    //申诉原因    private String appealReason;    //申诉人（关联user表主键）    private String userID;    //申诉情况(1,未受理;2,核实中;3,受理中;4,已处理)    private Integer situationAppeal;    //处理意见    private String processing;    private String positionTitle;    private String appealName;    private String appealTel;    private String positionName;    private String positionTel;    //图片对象    @StorageChild(service = "attachmentService",revColumn = "refID")    private List<Attachment> attachmentList ;    public String getNextAppealNo(){        Long ser = Long.parseLong(this.appealID.substring(1));        return "S"+(getFullNo(++ser));    }    private String getFullNo(Long num){        String str = "000000000"+num;        return str.substring(str.length()-10,str.length());    }}