package com.yskj.models;import com.yskj.aop.StorageChild;import com.yskj.models.auth.User;import jdk.nashorn.internal.ir.annotations.Ignore;import lombok.Data;import java.math.BigDecimal;//表名称 applySettlement@Datapublic class ApplySettlement extends BaseEntity {	private static final long serialVersionUID = 1L;	public ApplySettlement() {		super();	}	    //申请结算薪酬    private BigDecimal applyPay;    //结算原因    private String applyReason;    //申请人    private String userID;    //申请职位    private String positionID;    //处理意见    private Boolean handleOpinion;    //反馈原因    private String reason;    //申请结算日期    private String workDate;    //0等待支付 1已经支付 2不同意 3催结算    private Integer state;    @StorageChild(refColumn = "userID")    private User user ;    //职位的工作时间    private String positionWorkDate;    private String attachmentId;    @Ignore    @StorageChild(service = "attachmentService",refColumn = "attachmentId")    private Attachment attachment;    private String signIDS;    private String name;    private RewardSettlement rewardSettlement ;}