package com.yskj.models;import lombok.Data;import java.io.File;//表名称 attachment@Datapublic class Attachment extends BaseEntity {	private static final long serialVersionUID = 1L;	public Attachment() {		super();	}    //日期字符串    private String datestr;    //文件路径    private String path;    //文件名称    private String name;    //标题    private String title;    //类型 1：头像 2：证件 3：动态图片 4:验证码,5:校徽 9聊天    private Integer type;    //关联到主对象ID    private String refID;    private String absolutelyPath;    public String getAbsolutelyPath(){        return this.getPath()+ File.separator+this.getDatestr()+ File.separator+this.getName();    }}