package com.yskj.models;import com.yskj.aop.StorageChild;import lombok.Data;import java.math.BigDecimal;//表名称 bondtransaction@Datapublic class Bondtransaction extends BaseEntity {	private static final long serialVersionUID = 1L;	public Bondtransaction() {		super();	}	    //订单编号（关联入账表订单编号）    private String orderNumber;    //缴纳人（关联User表主键）    private String userID;    //缴纳职位（关联Position表主键）    private String positionID;    //缴纳人类型（1,兼职人员;2,雇佣方;）    private Integer payerType;    //微信订单    private String wxorderID;    //缴纳金额    private BigDecimal premiumMoney;    //已经退回    private Boolean isReturn = false ;    //标题    private String title;    private String mark;    //1，求职者保证金退还；2，求职者薪资收入；3，招聘者保证金退还；4，招聘者薪资发放,5，提现 ,6 求职人员违约赔付给招聘者，7招聘者赔付给求职人员    private Integer type;}