package com.yskj.models;import jdk.nashorn.internal.ir.annotations.Ignore;import lombok.Data;import java.math.BigDecimal;//表名称 city@Datapublic class City extends BaseEntity {	private static final long serialVersionUID = 1L;	public City() {		super();	}	    //拼音    private String pinyin;    //城市名称    private String cityName;    //邮编编码    private String zipCode;    //状态（1，开放;0，未开放）    private Boolean openState;    //类型(1,省;2,市;3,区;4,县 、街道)    private Integer cityType;    //是否为热门城市(0,否;1,是;)    private Boolean isHot;    //父节点    private String parentID;    //详细地址    private String mergerName;    //电话号码    private String cityCode;    //经度    private BigDecimal lng;    //纬度    private BigDecimal lat;    @Ignore    private Boolean isSelected;    @Ignore    private String refId;}