package com.yskj.models;import lombok.Data;import java.util.Date;//表名称 content@Datapublic class Content extends BaseEntity {	private static final long serialVersionUID = 1L;	public Content() {		super();	}	    //内容代码    private String contentCode;    //内容名称    private String contentName;    //内容类型（1,html;2,json;）    private Boolean contentType;    //内容状态(1,未启用;2,已启用)    private Boolean contentState;    //内容正文    private String contentText;    //内容描述    private String contentDesc;    //上级分类    private String parentID;}