package com.yskj.models;import com.yskj.aop.StorageChild;import lombok.Data;import org.springframework.format.annotation.DateTimeFormat;import java.util.Date;//表名称 document@Datapublic class Document extends BaseEntity {	private static final long serialVersionUID = 1L;	public Document() {		super();	}    //证件图片（证件图片是多张图片用”,”分割）    private String focumentImg;    //简历ID(关联Resume 表主键)    private String resumeID;    //有效日期    @DateTimeFormat(pattern = "yyyy-MM-dd")    private Date effective;    //名称    private String documentName;    //图片对象    @StorageChild(service = "attachmentService",refColumn = "focumentImg")    private Attachment attachment;}