package com.yskj.models;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.format.annotation.DateTimeFormat;import java.util.Date;//表名称 educational@Datapublic class Educational extends BaseEntity {	private static final long serialVersionUID = 1L;	public Educational() {		super();	}	    //学校名称    private String schoolName;    //专业    private String major;    //学历    private String education;    //学历等级    private Integer educationLeavl;    //开始时间    @DateTimeFormat(pattern = "yyyy-MM-dd")    private Date timeSlot;    //截止时间    @DateTimeFormat(pattern = "yyyy-MM-dd")    private Date timeEnd;    //在校经历    private String association;    //简历ID(关联Resume 表主键)    private String resumeID;}