package com.yskj.models;import com.yskj.aop.StorageChild;import com.yskj.models.auth.User;import jdk.nashorn.internal.ir.annotations.Ignore;import lombok.Data;import java.util.Date;import java.util.List;//表名称 evaluate@Datapublic class Evaluate extends BaseEntity {	private static final long serialVersionUID = 1L;	public Evaluate() {		super();	}	    //评价内容    private String evaluateContent;    //评价时间    private Date evaluateTime;    //等级0-10    private Integer evaluateLevel;    //求职者ID    private String userID;    //职位ID    private String positionID;    //状态false 待回复， true 已经回复    private Boolean state;    //图片路径    private String picture;    //回复    private String reply;    //图片对象    @StorageChild(service = "attachmentService",revColumn = "refID")    private List<Attachment> attachmentList ;    @Ignore    private String beenrecruitedID ;    @StorageChild(service = "" ,refColumn = "userID")    private User user ;    @StorageChild(service = "" ,refColumn = "createBy")    private User replyUser ;    @Ignore    @StorageChild(service = "",refColumn = "positionID")    private Position position ;}