package com.yskj.models;import com.yskj.aop.StorageChild;import jdk.nashorn.internal.ir.annotations.Ignore;import lombok.Data;import java.math.BigDecimal;//表名称 information@Datapublic class Information extends BaseEntity {    @Ignore    public static final Integer EMPLOYER = 2;    //雇佣方    @Ignore    public static final Integer JOB_SEEKER = 1; //求职者    private static final long serialVersionUID = 1L;	public Information() {		super();	}	    //二维码    private String qRCode;	//背景图片    private String bgpID;    //所属用户    private String userID;    //工作号    private String workNumber;    //简介    private String brief;    //当前角色  2雇佣方 1求职者    private Integer identityType;    //是否进行实名认证（企业认证）(1,已认证;0,未认证)    private Boolean authentication;    //支付密码    private String payPassword;    //微信头像    private String weixinHeadImgUrl ;    private Integer payType;  //1微信支付  2余额支付    private BigDecimal money; //余额    @Ignore    @StorageChild(service = "attachmentService",refColumn = "qRCode")    private Attachment qrCodeObj ;    @StorageChild(service = "attachmentService",refColumn = "bgpID")    private Attachment bgp ;    //在校生    private Boolean isStudents;    //学校    private String school;    //上次登录的工作号。    private String lastWorkNumber;    private Integer fullType ;}