package com.yskj.models;import com.yskj.aop.StorageChild;import jdk.nashorn.internal.ir.annotations.Ignore;import lombok.Data;import java.math.BigDecimal;import java.util.List;//表名称 invoice@Datapublic class Invoice extends BaseEntity {	private static final long serialVersionUID = 1L;	public Invoice() {		super();	}	    //订单号    private String orderNumber;    //抬头    private String title;    //金额    private BigDecimal money;    //内容    private String content;    //    private String userID;    //收件人ID    private String recipientsID;    //邮费    private BigDecimal postage;    //详情ID    private String detailID;    //税号    private String paragraph;    //状态 false 未通过或是审核中 , true  通过审核。    private Boolean status;    //包含多少条充值记录    private Integer counts ;    @StorageChild(refColumn = "recipientsID",service = "recipientsService")    private Recipients recipients ;    @StorageChild(refColumn = "detailID",service = "invoiceDetailService")    private InvoiceDetail invoiceDetail ;    //充值记录ID集合    private List<String> rechargeList ;    @Ignore    @StorageChild(revColumn = "invoiceID")    private List<Recharge> recharges ;}