package com.yskj.models;import com.yskj.aop.StorageChild;import com.yskj.models.auth.User;import lombok.Data;import java.util.Date;import java.util.List;//表名称 parttimeinformation@Datapublic class Parttimeinformation extends BaseEntity {	private static final long serialVersionUID = 1L;	public Parttimeinformation() {		super();	}	    //发布方(关联用户表主键)    private String userID;    //发布内容    private String releaseContent;    //发布时间    private Date releaseTime;    //发布图片    private String releasePicture;    @StorageChild(service = "attachmentService",revColumn = "refID")    private List<Attachment> attachmentList ;    @StorageChild(service = "",refColumn = "userID")    private User user ;    @StorageChild(service = "",refColumn = "concernID")    private Attention attention ;}