package com.yskj.models;import com.yskj.aop.StorageChild;import com.yskj.models.auth.User;import jdk.nashorn.internal.ir.annotations.Ignore;import lombok.Data;import java.util.List;//表名称 resume@Datapublic class Resume extends BaseEntity {	private static final long serialVersionUID = 1L;	public Resume() {		super();	}	    //简历标题    private String resumeTitle;    //头像    private String resumeHeadImg;    //姓名    private String resumeName;    //身份证    private String IDCard;    //性别(1,男;2,女)    private Integer sex;    //婚姻状况(1,未婚;2,已婚未育;3,已婚已育;)    private Integer marriage;    //手机号码    private String phoneNumber;    //学历等级    private Integer schoolLevel ;    //邮箱    private String email;    //现居地（关联地区表localtionInfo）    private String residence;    private Integer age ;    //自我评价    private String evaluation;    //所属用户（关联Information 表 主键）    private String userID;    //是否more（0不默认，1默认）    private Boolean isDefault;    //状态（0不公开，1公开）    private Boolean state;    //身高    private Integer height;    //体重(KG)    private Integer weight;    @StorageChild(service = "educationalService", revColumn = "resumeID")    private List<Educational> educationalList ;    @Ignore    @StorageChild(service = "attachmentService",refColumn = "resumeHeadImg")    private Attachment attachment;    private String imgPath;    @Ignore    @StorageChild(service = "",refColumn = "userID",revColumn = "userID")    private List<Intentiontype> intentiontypeList ;    @Ignore    @StorageChild(service = "",refColumn = "userID",revColumn = "userID")    private List<Intentionaddress> intentionaddress ;    @StorageChild(service = "",refColumn = "userID")    private User user;    @Ignore    @StorageChild(service = "workexperienceService",revColumn = "resumeID")    private List<Workexperience> workexperienceList ;    @Ignore    @StorageChild(service = "documentService",revColumn = "resumeID")    private List<Document> documentList ;}