package com.yskj.models;import com.yskj.aop.StorageChild;import lombok.Data;import java.math.BigDecimal;import java.util.ArrayList;import java.util.List;//表名称 scansettle@Datapublic class ScanSettle extends BaseEntity {	private static final long serialVersionUID = 1L;	public ScanSettle() {		super();	}	    //标题    private String title;    //日薪资    private BigDecimal dailySalary;    //二维码编码 8位 UUIDGenerator.getRandomString(8)生成    private String code;    //状态0待结算，1已结算    private Boolean status;    //多少天    private Integer days;    private Boolean open ;    private Integer size ;    private String positionID;    private Boolean isEdit;    @StorageChild(refColumn = "id")    private List<ScanSettleMember> scanSettleMemberList;    private Boolean isImport;  //是不是导入的    public void addScanSettleMember(ScanSettleMember scanSettleMember){        if(scanSettleMemberList==null){            scanSettleMemberList = new ArrayList<>();        }        this.scanSettleMemberList.add(scanSettleMember);    }}