package com.yskj.models;import com.yskj.aop.StorageChild;import lombok.Data;//表名称 school@Datapublic class School extends BaseEntity {	private static final long serialVersionUID = 1L;	public School() {		super();	}	    //学校名称    private String name;    //地址    private String address;    //校徽图片    private String badge;    private Boolean isStudents;    @StorageChild(service = "localtioninfoService",refColumn = "address")    private Localtioninfo schoolAddr;    private String keyword;    private Double distance;    private String workID;}