package com.yskj.models;import lombok.Data;import java.math.BigDecimal;import java.util.Date;//表名称 settlement@Datapublic class Settlement extends BaseEntity {	private static final long serialVersionUID = 1L;	public Settlement() {		super();	}	    //订单编号    private String settlementOrderNumber;    //备注    private String remarks;    //第三方状态 1已下订单，待支付，2已经支付成功，3支付失败    public final  static Integer DZF = 1;    public final  static Integer ZFCG = 2;    public final  static Integer ZFSB = 3;    private Integer settlementState;    //平台状态（1，成功;0失败）    private Boolean platformState;    //类型(1、保证金、2、薪资结算、3...)    public final static Integer BZJ = 1;    public final static Integer XZJS =2;    public final static Integer HHR =3;    public final static Integer HB =4;    private String type;    //所属用户（关联用户表主键）    private String userID;    //第三方平台 1微信公众号 2微信扫码支付 3...    public final static Integer WXGZH = 1;    public final static Integer WXSM = 2;    private Integer thirdPlatform;    //第三方订单编号    private String thirdOrderNumber;    //发起时间    private Date launchTime;    //支付时间    private Date payTime;    //回调时间    private Date callbackTime;    //所属职位（关联职位表主键）    private String refID ;    private BigDecimal money;    //是不是虚拟的     private Boolean isVirtual;}