package com.yskj.models;import com.yskj.aop.StorageChild;import lombok.Data;import java.math.BigDecimal;import java.util.List;//表名称 settlementpersongroup@Datapublic class SettlementPersonGroup extends BaseEntity {	private static final long serialVersionUID = 1L;    public SettlementPersonGroup() {        super();    }    //所有的id用逗号隔海    private String applyId;    //所有的金额    private BigDecimal totalMoney;    //大小    private Integer size;    //状态 false 待付款 true 已付款    private Boolean isPay;    private String positionID;    @StorageChild(revColumn = "refID")    private Wxorder wxorder;    private List<ApplySettlement> applySettlements;}