package com.yskj.models;import com.yskj.aop.StorageChild;import com.yskj.models.auth.User;import jdk.nashorn.internal.ir.annotations.Ignore;import lombok.Data;import java.math.BigDecimal;//表名称 settlementperson@Datapublic class Settlementperson extends BaseEntity {	private static final long serialVersionUID = 1L;	public Settlementperson() {		super();	}	    //结算兼职人员（关联User表主键）    private String userID;    //结算方(关联Position 表)    private String positionID;    //结算金额    private BigDecimal settlementMoney;    //结算日期    private String settlementDate;    //确认状态    private Boolean state;    //订单编号（关联入账表主键）    private String settlementID;    @Ignore    @StorageChild(service = "",refColumn = "userID")    private User user ;    @StorageChild(refColumn = "settlementID",revColumn = "refID")    private  Settlement settlement;    @Ignore    @StorageChild(refColumn = "positionID")    private Position position ;    private String  title;    private BigDecimal sxf;}