package com.yskj.models;import com.yskj.aop.StorageChild;import com.yskj.models.auth.User;import jdk.nashorn.internal.ir.annotations.Ignore;import lombok.Data;import java.util.Date;import java.util.List;import java.util.Map;//表名称 signin@Datapublic class Signin extends BaseEntity {	private static final long serialVersionUID = 1L;	public Signin() {		super();	}	    //签到人（关联user表主键）    private String userID;    //签到地点（关联地址信息表）    private String signAddress;    //所属职位（关联Position 表主键）    private String positionID;    //签到时间    private Date signTime;    //状态(0,未确认;1,已确认到岗; 2，确认已工作结束,3,确认薪资待支付，4：已支付，9确认未到岗)    private Integer state;    //确认时间    private Date confirmationTime;    //签到类型  WORK("工作",4),INTERVIEW("面试签到",2),TRAIN("培训签到",3);    private Integer signinType;    private String attachmentId;    @StorageChild(service = "localtioninfoService",refColumn = "signAddress")    private Localtioninfo address;    @StorageChild(service = "attachmentService",revColumn = "refID")    private List<Attachment> attachmentList ;    private String mark;    @Ignore    @StorageChild(service = "",refColumn = "positionID")    private Beenrecruited beenrecruited;    @Ignore    @StorageChild(service = "",refColumn = "userID")    private User user;    @Ignore    private Boolean hasUrge;    @Ignore    private Integer num;    @Ignore    private String dates;    @Ignore    private String ids;    //赏金数据存放。    private Map rewardMap;    //签退时间    private Date signBack;    //签退地址    private String signBackAddr;    @StorageChild(service = "localtioninfoService",refColumn = "signBackAddr")    private Localtioninfo backAddress;    private String backAttachment;    private List<Attachment> backAttachmentList ;    private String title;}