package com.yskj.models;import com.yskj.exception.IJobException;import com.yskj.models.enums.Examine;import com.yskj.utils.DateUtils;import com.yskj.utils.SignUtils;import lombok.Data;//表名称 worklist@Datapublic class WorkList extends BaseEntity {	private static final long serialVersionUID = 1L;	public WorkList() {		super();	}	    //订单编号    private String code;    //类型 01身份认证 02充值    private String type;    //状态 1待审核 2审核通过 3审核不通过    private Integer status;    //关联ID    private String refID;    //错误信息    private String msg;    //地址    private String url;    //标题    private String title;    //审核人    private String auditor;    //回调    private String callback;    public void  updateType(Examine examine)throws IJobException{        this.code = obtainCode(examine);        this.title = Examine.getTitle(examine.toString())+"审核";    }    public String  obtainCode(Examine examine){        return Examine.getCode(examine.toString())+DateUtils.getCurrTime()+ SignUtils.getRandomStringByLength(8);    }    public static void main(String[] args) {        WorkList workList  = new WorkList();        try {            workList.updateType(Examine.IDCard);        } catch (IJobException e) {            e.printStackTrace();        }        System.out.println(workList);    }    public String getStatusCN(){        return this.status==1?"待审核":(this.status==2?"审核通过":"审核不通过");    }    public String getCreateTimeLocal(){        return DateUtils.format(this.getCreateTime(),"yyyy-MM-dd hh:mm:ss");    }    public String getUpdateTimeLocal(){        return DateUtils.format(this.getUpdateTime(),"yyyy-MM-dd hh:mm:ss");    }}