package com.yskj.models;import com.fasterxml.jackson.annotation.JsonFormat;import lombok.Data;import org.springframework.format.annotation.DateTimeFormat;import java.util.Date;//表名称 workexperience@Datapublic class Workexperience extends BaseEntity {	private static final long serialVersionUID = 1L;	public Workexperience() {		super();	}	    //公司名称    private String companyName;    //职位    private String jobName;    //开始时间    @DateTimeFormat(pattern = "yyyy-MM-dd")    private Date startTime;    //结束时间    @DateTimeFormat(pattern = "yyyy-MM-dd")    private Date endTime;    //工作内容    private String jobContent;    //简历ID(关联Resume 表主键)    private String resumeID;}