package com.yskj.service;import com.yskj.dao.AdverDao;import com.yskj.models.Adver;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;@Servicepublic class AdverService extends AbstractService {	@Autowired    private AdverDao adverDao;	public AdverService() {		super();	}	@Override	public AdverDao getDao() {		return this.adverDao;	}	public List<Adver> getAdverInfo(Map map){ return adverDao.getAdverInfo(map); }}