package com.yskj.service;import com.yskj.dao.CityDao;import com.yskj.models.City;import com.yskj.models.Huntingtype;import com.yskj.models.QueryParam;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.ArrayList;import java.util.List;@Servicepublic class CityService extends AbstractService {	@Autowired    private CityDao cityDao;	public CityService() {		super();	}	@Override	public CityDao getDao() {		return this.cityDao;	}	public List<City> getHistorCityList (String userID){		return cityDao.getHistorCityList(userID);	}	public List<City> getHotCityList(){		return cityDao.getHotCityList();	}	public List<City> getCityList(){		return cityDao.getCityList();	}	public List<City> getMyCityRegion(QueryParam queryParam){		return cityDao.getMyCityRegion(queryParam);	}	public List<City> getCanJobCity(QueryParam queryParam){		queryParam.put("openState",true);		List<City> list = null;		try {			list  = this.findList(queryParam);		} catch (Exception e) {			e.printStackTrace();		}finally {			if(list==null){				list = new ArrayList<City>();			}			City city = new City();			city.setCityName("全部");			city.setId("");			list.add(0,city);		}		return list;	}}