package com.yskj.service;import com.yskj.dao.EducationalDao;import com.yskj.models.Educational;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class EducationalService extends AbstractService {	@Autowired    private EducationalDao educationalDao;	public EducationalService() {		super();	}	@Override	public EducationalDao getDao() {		return this.educationalDao;	}	public Educational findEducationnalForEducation(String id){ return educationalDao.findEducationnalForEducation(id); }}