package com.yskj.service;import com.yskj.dao.HuntingtypeDao;import com.yskj.models.Huntingtype;import com.yskj.models.QueryParam;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.ArrayList;import java.util.List;@Servicepublic class HuntingtypeService extends AbstractService {	@Autowired    private HuntingtypeDao huntingtypeDao;	public HuntingtypeService() {		super();	}	@Override	public HuntingtypeDao getDao() {		return this.huntingtypeDao;	}	public List<Huntingtype> findAllType(QueryParam queryParam){		List<Huntingtype> list = null;		try {			list  = this.findList(queryParam);		} catch (Exception e) {			e.printStackTrace();		}finally {			if(list==null){				list = new ArrayList<Huntingtype>();			}		}		return list;	}}