package com.yskj.service;import com.yskj.dao.InformationDao;import com.yskj.models.Information;import com.yskj.models.Position;import com.yskj.models.QueryParam;import com.yskj.models.auth.User;import com.yskj.service.auth.UserService;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;@Servicepublic class InformationService extends AbstractService {	@Autowired    private InformationDao informationDao;	@Autowired	private UserService userService;	public InformationService() {		super();	}	@Override	public InformationDao getDao() {		return this.informationDao;	}	public Long updateSomeInfo(User user, Information information) throws Exception{		return this.update(information)+userService.update(user);	}	public Integer findOneUser(Map map){		return informationDao.findOneUser(map);	}	public Integer updateLoginPassword(Map map){		return informationDao.updateLoginPassword(map);	}	public Long checkUser(Map map) {		return informationDao.checkUser(map);	}	public List<Position> findListForUser(Map queryMap){		return informationDao.findListForUser(queryMap);	}	public List<Position> findListNoContainCity(Map queryMap){	    return informationDao.findListNoContainCity(queryMap);    }    public List<Position> findListNoContainType(Map querymap){	    return informationDao.findListNoContainType(querymap);    }    public Information getInforWithQRCode(String id){		return informationDao.getInforWithQRCode(id);	}	public Information informapOne(QueryParam queryParam){    	return informationDao.informapOne(queryParam);	}}