package com.yskj.service;import com.yskj.dao.IntentionaddressDao;import com.yskj.models.PageParam;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class IntentionaddressService extends AbstractService {	@Autowired    private IntentionaddressDao intentionaddressDao;	public IntentionaddressService() {		super();	}	@Override	public IntentionaddressDao getDao() {		return this.intentionaddressDao;	}	public PageParam mapPage(PageParam pageParam) throws Exception {		pageParam.setList(this.getDao().mapPage(pageParam));		return  pageParam;	}}