package com.yskj.service;import com.yskj.dao.MetroDao;import com.yskj.models.Metro;import com.yskj.models.QueryParam;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class MetroService extends AbstractService {	@Autowired    private MetroDao metroDao;	public MetroService() {		super();	}	@Override	public MetroDao getDao() {		return this.metroDao;	}	public List<Metro> getMyCityMetro(QueryParam queryParam){		return metroDao.getMyCityMetro(queryParam);	}}