package com.yskj.service;import com.yskj.dao.MycardDao;import com.yskj.service.base.AbstractService;import com.yskj.utils.UUIDGenerator;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class MycardService extends AbstractService {	@Autowired    private MycardDao mycardDao;	public MycardService() {		super();	}	public String nextCode(String type){		return type+ UUIDGenerator.getRandomInteger(12-type.length());	}	@Override	public MycardDao getDao() {		return this.mycardDao;	}}