package com.yskj.service;import com.yskj.dao.MycityDao;import com.yskj.models.Mycity;import com.yskj.models.QueryParam;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class MycityService extends AbstractService {	@Autowired    private MycityDao mycityDao;	public MycityService() {		super();	}	@Override	public MycityDao getDao() {		return this.mycityDao;	}	public List<Mycity> mapList(QueryParam queryParam) throws Exception {		return this.getDao().mapList(queryParam);	}	public Mycity mapOne(QueryParam queryParam){		return this.getDao().mapOne(queryParam);	}}