package com.yskj.service;import com.yskj.dao.ParttimeinformationDao;import com.yskj.models.Attachment;import com.yskj.models.PageParam;import com.yskj.models.Parttimeinformation;import com.yskj.models.QueryParam;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class ParttimeinformationService extends AbstractService {	@Autowired    private ParttimeinformationDao parttimeinformationDao;	@Autowired	private AttachmentService attachmentService ;	public ParttimeinformationService() {		super();	}	@Override	public ParttimeinformationDao getDao() {		return this.parttimeinformationDao;	}	public PageParam loadMyPartTimeInformationPage(PageParam pageParam) throws Exception{		List<Parttimeinformation> parttimeinformationList = parttimeinformationDao.loadMyPartTimeInformationPage(pageParam);		QueryParam queryParam = new QueryParam("isDeleted",false);		queryParam.setOrderByClause("ORDER BY a.createTime DESC");		for (Parttimeinformation parttimeinformation : parttimeinformationList){			queryParam.put("refID",parttimeinformation.getId());			List<Attachment> attachmentList = attachmentService.findList(queryParam);			parttimeinformation.setAttachmentList(attachmentList);		}		pageParam.setList(parttimeinformationList);		return pageParam;	}	public Parttimeinformation getContentAndPhotos(QueryParam queryParam){		return getDao().getContentAndPhotos(queryParam);	}}