package com.yskj.service;import com.yskj.dao.PostForBrokerDao;import com.yskj.models.PageParam;import com.yskj.models.PostForBroker;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class PostForBrokerService extends AbstractService {	@Autowired    private PostForBrokerDao postForBrokerDao;	public PostForBrokerService() {		super();	}	@Override	public PostForBrokerDao getDao() {		return this.postForBrokerDao;	}	public PageParam getPostForBrokerInfoListPage(PageParam pageParam){		pageParam.setList(this.getDao().getPostForBrokerInfoListPage(pageParam));		return pageParam;	}	public PostForBroker getPostForBrokerFullInfo(String id){		return getDao().getPostForBrokerFullInfo(id);	}}