package com.yskj.service;import com.yskj.dao.PostLabelDao;import com.yskj.models.PageParam;import com.yskj.models.QueryParam;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.HashMap;import java.util.List;@Servicepublic class PostLabelService extends AbstractService {	@Autowired    private PostLabelDao postLabelDao;	public PostLabelService() {		super();	}	@Override	public PostLabelDao getDao() {		return this.postLabelDao;	}	public List<HashMap> findListByCreate(QueryParam queryParam) {		return this.postLabelDao.findListByCreate(queryParam);	}}