package com.yskj.service;import com.yskj.dao.PostDao;import com.yskj.models.Post;import com.yskj.models.QueryParam;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class PostService extends AbstractService {	@Autowired    private PostDao postDao;	public PostService() {		super();	}	@Override	public PostDao getDao() {		return this.postDao;	}	public Post postJobInfo(QueryParam queryParam){		return getDao().postJobInfo(queryParam);	}}