package com.yskj.service;import com.yskj.dao.ResumeDao;import com.yskj.models.PageParam;import com.yskj.models.QueryParam;import com.yskj.models.Resume;import com.yskj.service.base.AbstractService;import com.yskj.utils.IJobSecurityUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;@Servicepublic class ResumeService extends AbstractService {	@Autowired    private ResumeDao resumeDao;	public ResumeService() {		super();	}	@Override	public ResumeDao getDao() {		return this.resumeDao;	}	public List<Resume> personLikeSearch(Map map){		return resumeDao.personLikeSearch(map);	}	public List<Map> personSearch(Map map){		return resumeDao.personSearch(map);	}	public List<Map> findTypeNameWithUserID(String userID) {		return resumeDao.findTypeNameWithUserID(userID);	}	public Integer updateWithUserID(String userID){		try{			resumeDao.updateWithUserID(userID);		}catch (Exception e ){			e.printStackTrace();			return 0;		}		return 1;	}	public Resume getMyResumeAllInfo(String id) {		return resumeDao.getMyResumeAllInfo(id);	}	public PageParam mySeekerPage(PageParam pageParam){		pageParam.setList(resumeDao.mySeekerPage(pageParam));		return pageParam;	}	public PageParam findOtherResumePage(PageParam pageParam){		pageParam.setList(resumeDao.findOtherResumePage(pageParam));		return pageParam;	}	/**	 * 招聘者查看求职者简历时调用该方法查询数据库的简历信息	 * @param id 简历ID	 * @return	 */	public Resume getResumeForRecruit(String id){		return resumeDao.getResumeForRecruit(id);	}	public Resume selectResumeNoReq(String id){		return resumeDao.selectResumeNoReq(id);	}	public PageParam searchPersonResumePage(PageParam pageParam){		pageParam.setList(resumeDao.searchPersonResumePage(pageParam));		return pageParam;	}	public void  setDefault(Resume resume){		QueryParam queryParam = new QueryParam("userID", IJobSecurityUtils.getLoginUserId());		queryParam.put("isDefault",true);		try {			long num   = this.count(queryParam);			if(num==0){				resume.setIsDefault(true);			}		} catch (Exception e) {			e.printStackTrace();		}	}	public List<String> mySeekerPageOfUserID(PageParam pageParam){		return resumeDao.mySeekerPageOfUserID(pageParam);	}}