package com.yskj.service;import com.yskj.dao.SalaryImportDao;import com.yskj.models.QueryParam;import com.yskj.models.SalaryImport;import com.yskj.models.ScanSettle;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class SalaryImportService extends AbstractService {	@Autowired    private SalaryImportDao salaryImportDao;	public SalaryImportService() {		super();	}	@Override	public SalaryImportDao getDao() {		return this.salaryImportDao;	}	public List<SalaryImport> salaryImportList(QueryParam queryParam)throws Exception{		return this.getDao().salaryImportList(queryParam);	}	public void deleteSalaryInfoList(QueryParam queryParam){		getDao().deleteSalaryInfoList(queryParam);	}}