package com.yskj.service;import com.yskj.dao.ScanSettleMemberDao;import com.yskj.models.QueryParam;import com.yskj.models.excel.PayInfo;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;@Servicepublic class ScanSettleMemberService extends AbstractService {	@Autowired    private ScanSettleMemberDao scanSettleMemberDao;	public ScanSettleMemberService() {		super();	}	@Override	public ScanSettleMemberDao getDao() {		return this.scanSettleMemberDao;	}	public List<Map> findWaitRushList(QueryParam queryParam){		return scanSettleMemberDao.findWaitRushList(queryParam);	}	public Long findWaitRushListCount(QueryParam queryParam){		return scanSettleMemberDao.findWaitRushListCount(queryParam);	}	public Long findScanYJSListCount(QueryParam queryParam){		return scanSettleMemberDao.findScanYJSListCount(queryParam);	}	public List<Map> findScanYJSList(QueryParam queryParam){		return scanSettleMemberDao.findScanYJSList(queryParam);	}	public void updateSettlementStatus(String scanID){		getDao().updateSettlementStatus(scanID);	}	public List<PayInfo> getMyHistoryPayInfoDetails(String scanID){		return getDao().getMyHistoryPayInfoDetails(scanID);	}	public void deleteSalaryInfo(String scanID){		this.getDao().deleteSalaryInfo(scanID);	}	public List<Map> loadRecentPay(QueryParam queryParam){		return getDao().loadRecentPay(queryParam);	}	public void deleteLoadRecentPay(String scanID){		getDao().deleteLoadRecentPay(scanID);	}}