package com.yskj.service;import com.yskj.dao.SchoolDao;import com.yskj.models.PageParam;import com.yskj.models.QueryParam;import com.yskj.models.School;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class SchoolService extends AbstractService {	@Autowired    private SchoolDao schoolDao;	public SchoolService() {		super();	}	@Override	public SchoolDao getDao() {		return this.schoolDao;	}	public List<School> attachedSchoolPage(PageParam queryParam)throws Exception{		return schoolDao.attachedSchoolPage(queryParam);	}}