package com.yskj.service;import com.yskj.dao.SearchlogDao;import com.yskj.models.Searchlog;import com.yskj.service.base.AbstractService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class SearchlogService extends AbstractService {	@Autowired    private SearchlogDao searchlogDao;	public SearchlogService() {		super();	}	@Override	public SearchlogDao getDao() {		return this.searchlogDao;	}	public List<Searchlog> findHotList() {		return getDao().findHotList();	}}